def main(input)

  #starting at 5, so we need to start at array1[1] (row = array1, col = 1)
  $row=1
  $col=1
  $loc=5
  $code=[]

  def getloc

  #the numpad, in array form
  array0=[1,2,3]
  array1=[4,5,6]
  array2=[7,8,9]

  case $row
  when 0
    loc = array0[$col]
  when 1
    loc = array1[$col]
  when 2
    loc = array2[$col]
  end
   return loc
  end

  input = input.split("\n")

  input.each do |i|
    line = i.split('')
    line.each do |x|
      case x
      when "U"
        $row == 0 ? $row = 0 : $row = $row-1
        $loc = getloc
      when "D"
        $row == 2 ? $row = 2 : $row = $row+1
        $loc = getloc
      when "L"
        $col == 0 ? $col = 0 : $col = $col-1
        $loc = getloc
      when "R"
        $col == 2 ? $col = 2 : $col = $col+1
        $loc = getloc
      end
    end
    $code << $loc
  end
 return $code.join('')
end

puts main("



")
