def main(input)

  #starting at 5, so we need to start at array2[0] (row = array2, col = 0)
  $row=2
  $col=0
  $loc=5
  $code=[]

  def getloc

  #the numpad, in array form
  array0=["X","X",1,"X","X"]
  array1=["X",2,3,4,"X"]
  array2=[5,6,7,8,9]
  array3=["X","A","B","C","X"]
  array4=["X","X","D","X","X"]

    case $row
    when 0
      loc = array0[$col]
    when 1
      loc = array1[$col]
    when 2
      loc = array2[$col]
    when 3
      loc = array3[$col]
    when 4
      loc = array4[$col]
    end
     return loc
  end

  input = input.split("\n")

  input.each do |i|
    line = i.split('')
    line.each do |x|
      case x
      when "U"
        $loc = getloc and next if $row == 0
        $row = $row - 1
        $row = $row + 1 if getloc == "X"
        $loc = getloc
      when "D"
        $loc = getloc and next if $row == 4
        $row = $row + 1
        $row = $row - 1 if getloc == "X"
        $loc = getloc
      when "L"
        $loc = getloc and next if $col == 0
        $col = $col -1
        $col = $col + 1 if getloc == "X"
        $loc = getloc
      when "R"
        $loc = getloc and next if $col == 4
        $col = $col + 1
        $col = $col - 1 if getloc == "X"
        $loc = getloc
      end
    end
    $code << $loc
  end
 return $code.join('')
end

puts main("



